import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [departmentId, setDepartmentId] = useState('');
  const [location, setLocation] = useState('');
  const [departments, setDepartments] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchDepartments = async () => {
      const res = await axios.get('http://localhost:5000/api/departments');
      setDepartments(res.data);
    };
    fetchDepartments();
  }, []);

  const handleAddPhone = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/phones', {
        phone_number: phoneNumber,
        department_id: departmentId,
        location: location,
      });
      navigate('/phone-list'); // Chuyển về trang danh sách sau khi thêm thành công
    } catch (err) {
      console.error('Lỗi khi thêm số điện thoại', err);
    }
  };

  return (
    <div>
      <h2>Thêm Số Điện Thoại Mới</h2>
      <form onSubmit={handleAddPhone}>
        <input 
          type="text" 
          placeholder="Số điện thoại"
          value={phoneNumber}
          onChange={(e) => setPhoneNumber(e.target.value)}
          required
        />
        <select 
          value={departmentId}
          onChange={(e) => setDepartmentId(e.target.value)}
          required
        >
          <option value="">Chọn bộ phận</option>
          {departments.map(dept => (
            <option key={dept.id} value={dept.id}>
              {dept.name}
            </option>
          ))}
        </select>
        <input 
          type="text" 
          placeholder="Vị trí"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <button type="submit">Thêm Số Điện Thoại</button>
      </form>
    </div>
  );
};

export default Dashboard;
