import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const PhonesManagement = () => {
    const [phoneNumber, setPhoneNumber] = useState('');
    const [departmentId, setDepartmentId] = useState('');
    const [location, setLocation] = useState('');
    const [departments, setDepartments] = useState([]);
    const navigate = useNavigate();
    const [editingId, setEditingId] = useState(null);
    const [users, setUsers] = useState([]);
    const [phones, setPhones] = useState([]);
    

    useEffect(() => {
        fetchUsers();
        fetchPhones();
    }, []);

    const fetchUsers = async () => {
        const res = await axios.get('http://localhost:5000/api/users');
        setUsers(res.data);
    };
    useEffect(() => {
        const fetchDepartments = async () => {
          const res = await axios.get('http://localhost:5000/api/departments');
          setDepartments(res.data);
        };
        fetchDepartments();
      }, []);

    const fetchPhones = async () => {
        const res = await axios.get('http://localhost:5000/api/phones');
        setPhones(res.data);
    };
    
    handleDelete = async (id) => {

        if (window.confirm('Bạn chắc chắn muốn xóa?')) {
            await axios.delete(`http://localhost:5000/api/phones/${id}`);
            fetchPhones();
        }
    }
    handleEdit = (phone) => {

        setEditingId(phone.id);
        setPhoneNumber(phone.phone_number);
        setDepartmentId(phone.department_id);
    }

    return (
        <div className="container mt-4">
            <h2>Phones Management</h2>
            <form onSubmit={handleSubmit} className="mb-4">
                <input
                    type="text"
                    className="form-control mb-2"
                    placeholder="Số điện thoại mới"
                    value={phoneNumber}
                    onChange={(e) => setPhoneNumber(e.target.value)}
                />
                <select 
                    value={departmentId}
                    onChange={(e) => setDepartmentId(e.target.value)}
                    required
                >
                    <option value="">Chọn bộ phận</option>
                    {departments.map(dept => (
                        <option key={dept.id} value={dept.id}>
                            {dept.name}
                        </option>
                    ))}
                </select>
                <button type="submit" className="btn btn-primary">
                    {editingId ? 'Cập nhật' : 'Thêm mới'}
                </button>
            </form>

            <table className="table">
                <thead>
                    <tr>
                        <th>Tên người dùng</th>
                        <th>Số điện thoại</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    
                    {phones.map(phone => (
                        <tr key={phone.id}>
                            <td>{phone.user_name}</td>
                            <td>{phone.phone_number}</td>
                            <td>
                                <button 
                                    className="btn btn-sm btn-warning me-2"
                                    onClick={() => handleEdit(phone)}
                                >
                                    Sửa
                                </button>
                                <button 
                                    className="btn btn-sm btn-danger"
                                    onClick={() => handleDelete(phone.id)}
                                >
                                    Xóa
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default PhonesManagement;
