import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const UserManagement = () => {
    const [name, setName] = useState('');
    const [phoneId, setPhoneId] = useState('');
    const [users, setUsers] = useState([]);
    const [phones, setPhones] = useState([]);
    const [editingId, setEditingId] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetchUsers();
        fetchPhones();
    }, []);

    const fetchUsers = async () => {
        const res = await axios.get('http://localhost:5000/api/users');
        setUsers(res.data);
    };

    const fetchPhones = async () => {
        const res = await axios.get('http://localhost:5000/api/phones');
        setPhones(res.data);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!name || !phoneId) return alert('Vui lòng điền đầy đủ thông tin');
        
        if (editingId) {
            await axios.put(`http://localhost:5000/api/users/${editingId}`, { name, phone_id: phoneId });
            alert('Cập nhật thành công');
        } else {
            await axios.post('http://localhost:5000/api/users', { name, phone_id: phoneId });
            alert('Thêm thành công');
        }
        fetchUsers();
        setName('');
        setPhoneId('');
        setEditingId(null);
    };

    const handleDelete = async (id) => {
        if (window.confirm('Bạn chắc chắn muốn xóa?')) {
            await axios.delete(`http://localhost:5000/api/users/${id}`);
            fetchUsers();
        }
    };

    const handleEdit = (user) => {
        setEditingId(user.id);
        setName(user.name);
        setPhoneId(user.phone_id);
    };

    return (
        <div className="container mt-4">
            <h2>Quản lý Người dùng</h2>
            <form onSubmit={handleSubmit} className="mb-4">
                <input
                    type="text"
                    className="form-control mb-2"
                    placeholder="Tên người dùng"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
                <select
                    className="form-control mb-2"
                    value={phoneId}
                    onChange={(e) => setPhoneId(e.target.value)}
                >
                    <option value="">Chọn số điện thoại</option>
                    {phones.map(phone => (
                        <option key={phone.id} value={phone.id}>
                            {phone.phone_number} ({phone.location})
                        </option>
                    ))}
                </select>
                <button type="submit" className="btn btn-primary">
                    {editingId ? 'Cập nhật' : 'Thêm mới'}
                </button>
            </form>

            <table className="table">
                <thead>
                    <tr>
                        <th>Tên người dùng</th>
                        <th>Số điện thoại</th>
                        <th>Hành động</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td>{user.name}</td>
                            <td>{user.phone_number}</td>
                            <td>
                                <button 
                                    className="btn btn-sm btn-warning me-2"
                                    onClick={() => handleEdit(user)}
                                >
                                    Sửa
                                </button>
                                <button
                                    className="btn btn-sm btn-danger"
                                    onClick={() => handleDelete(user.id)}
                                >
                                    Xóa
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserManagement;
