import React, { useState } from 'react';
import axios from 'axios';

function Dashboard() {
  const [newPhone, setNewPhone] = useState({
    name: '',
    number: '',
    department: '',
    area: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewPhone({ ...newPhone, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/phones', newPhone, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
        },
      });
      // reset form
      setNewPhone({
        name: '',
        number: '',
        department: '',
        area: '',
      });
      alert('Thêm số điện thoại thành công!');
    } catch (err) {
      console.error('Lỗi thêm số điện thoại', err);
    }
  };

  return (
    <div className="container">
      <h2>Thêm số điện thoại</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Tên người sử dụng</label>
          <input
            type="text"
            className="form-control"
            name="name"
            value={newPhone.name}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Số điện thoại</label>
          <input
            type="text"
            className="form-control"
            name="number"
            value={newPhone.number}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Bộ phận</label>
          <input
            type="text"
            className="form-control"
            name="department"
            value={newPhone.department}
            onChange={handleChange}
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Khu vực</label>
          <input
            type="text"
            className="form-control"
            name="area"
            value={newPhone.area}
            onChange={handleChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Thêm
        </button>
      </form>
    </div>
  );
}

export default Dashboard;
