// src/components/Navbar.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = ({ isAdmin, adminName, onLogout, keyword, setKeyword, departmentList, selectedDept, setSelectedDept }) => {
    const navigate = useNavigate();

  return (
    <div className="bg-blue-600 p-4 flex justify-between items-center sticky top-0 z-50">
      <h1 className="text-white text-2xl font-bold cursor-pointer" onClick={() => navigate('/')}>
        Phone Book
      </h1>

      <div>
        {isAdmin ? (
          <button
            className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded"
            onClick={onLogout}
          >
            Logout
          </button>
        ) : (
          <button
            className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded"
            onClick={() => navigate('/login')}
          >
            Admin Login
          </button>
        )}
      </div>
    </div>
  );
};

export default Navbar;
