// src/components/Navbar.jsx
import React from 'react';
import { useNavigate } from 'react-router-dom';

const Navbar = ({ isAdmin, adminName, onLogout, keyword, setKeyword, departmentList, selectedDept, setSelectedDept }) => {
  const navigate = useNavigate();

  return (
    <div className="bg-white shadow-md p-3 flex flex-col md:flex-row md:items-center md:justify-between sticky top-0 z-50">
      {/* Logo b√™n tr√°i */}
      <div className="text-2xl font-bold text-blue-600 cursor-pointer mb-2 md:mb-0" onClick={() => navigate('/')}>
        üìû Phone Book
      </div>

      {/* √î t√¨m ki·∫øm v√† ch·ªçn ph√≤ng ban ·ªü gi·ªØa */}
      <div className="flex flex-col md:flex-row md:items-center gap-2 w-full md:w-auto">
        <input
          type="text"
          placeholder="T√¨m ki·∫øm..."
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
          className="border p-2 rounded w-full md:w-64"
        />
        <select
          value={selectedDept}
          onChange={(e) => setSelectedDept(e.target.value)}
          className="border p-2 rounded w-full md:w-48"
        >
          <option value="">T·∫•t c·∫£ b·ªô ph·∫≠n</option>
          {departmentList.map((dept) => (
            <option key={dept} value={dept}>
              {dept}
            </option>
          ))}
        </select>
      </div>

      {/* Ph·∫ßn admin b√™n ph·∫£i */}
      <div className="flex items-center gap-3 mt-2 md:mt-0">
        {isAdmin ? (
          <>
            <span className="text-gray-700 font-semibold">{adminName}</span>
            <span
              className="text-red-500 cursor-pointer hover:underline"
              onClick={onLogout}
            >
              Logout
            </span>
          </>
        ) : (
          <span
            className="text-blue-500 cursor-pointer hover:underline"
            onClick={() => navigate('/login')}
          >
            Admin Login
          </span>
        )}
      </div>
    </div>
  );
};

export default Navbar;
