import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PhoneCard from '../components/PhoneCard';
import { Link } from 'react-router-dom';

function PhoneList() {
  const [phones, setPhones] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [selectedDept, setSelectedDept] = useState('');
  const [keyword, setKeyword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  

  useEffect(() => {
    fetchPhones();
    fetchDepartments();

    // Kiểm tra nếu đã login Admin thì set isAdmin = true
    const token = localStorage.getItem('token');
    if (token) {
      setIsAdmin(true);
    }
  }, []);

  const fetchPhones = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/phones');
      setPhones(res.data);
    } catch (err) {
      console.error('Lỗi fetch phones', err);
    }
  };

  const fetchDepartments = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/departments');
      setDepartments(res.data);
    } catch (err) {
      console.error('Lỗi fetch departments', err);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    let query = `http://localhost:5000/api/phones/search?keyword=${keyword}&department=${selectedDept}`;
    const res = await axios.get(query);
    setPhones(res.data);
  };

  const handleDeptChange = async (e) => {
    const dept = e.target.value;
    setSelectedDept(dept);
  
    // Gọi API ngay khi chọn bộ phận
    let query = `http://localhost:5000/api/phones/search?keyword=${keyword}&department=${dept}`;
    const res = await axios.get(query);
    setPhones(res.data);
  };

  const handleEdit = (phone) => {
    console.log('Sửa:', phone);
    // TODO: mở Modal edit
  };

  const handleDelete = async (id) => {
    if (window.confirm('Bạn chắc chắn muốn xóa?')) {
      try {
        await axios.delete(`http://localhost:5000/api/phones/${id}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        });
        fetchPhones(); // refresh lại list
      } catch (err) {
        console.error('Lỗi xóa phone', err);
      }
    }
  };

  return (
    
    <><nav className="navbar navbar-expand-lg navbar-light bg-light fixed-top shadow-sm">
      <div className="container-fluid">
        <Link className="navbar-brand fw-bold" to="/">PhoneBook</Link>

        <div className="collapse navbar-collapse" id="navbarContent">
          {/* Search form */}
          <form className="d-flex ms-3 flex-grow-1" onSubmit={handleSearch}>
          <input
                type="text"
                className="form-control"
                placeholder="Tìm kiếm theo tên, số điện thoại, bộ phận, vị trí..."
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)} />
            <select
              className="form-select me-2"
              style={{ maxWidth: '200px' }}
              value={selectedDept} onChange={handleDeptChange}
            >
              <option value="">Tất cả bộ phận</option>
                {departments.map((dept) => (
                  <option key={dept.id} value={dept.name}>
                    {dept.name}
                  </option>
              ))}
            </select>
            <button className="btn btn-outline-primary" type="submit">Tìm</button>
          </form>

          {/* Admin Info */}
          <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
            {isAdmin ? (
              <>
                <li className="nav-item d-flex align-items-center me-3">
                  <span className="nav-link">Admin</span>
                </li>
                <li className="nav-item">
                  <button className="btn btn-outline-danger">
                    Đăng xuất
                  </button>
                </li>
              </>
            ) : (
              <li className="nav-item">
                <Link className="btn btn-outline-success" to="/login">
                  Đăng nhập
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
    <div className="container py-4">
        {/* Thanh Search */}
        <form className="mb-4 flex" onSubmit={handleSearch}>
          <div className="row">
            <div className="col-md-8">
              <input
                type="text"
                className="form-control"
                placeholder="Tìm kiếm theo tên, số điện thoại, bộ phận, vị trí..."
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)} />
            </div>
            <div className="col-md-4">
              <select className="form-select" value={selectedDept} onChange={handleDeptChange}>
                <option value="">Tất cả bộ phận</option>
                {departments.map((dept) => (
                  <option key={dept.id} value={dept.name}>
                    {dept.name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        </form>

        {/* Danh sách phones */}
        <div className="row">
          {phones.map((phone) => (
            <div key={phone.id} className="col-md-3 mb-3">
              <PhoneCard
                phone={phone}
                isAdmin={isAdmin}
                onEdit={() => handleEdit(phone)}
                onDelete={() => handleDelete(phone.id)} />
            </div>
          ))}
        </div>
      </div></>
  );
}

export default PhoneList;
