import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import PhoneCard from '../components/PhoneCard';
import { isAdminLoggedIn, logoutAdmin } from '../utils/auth';
import { toast } from 'react-toastify';

function PhoneList() {
  const [phones, setPhones] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [selectedDept, setSelectedDept] = useState('');
  const [keyword, setKeyword] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);
  const [editingPhone, setEditingPhone] = useState(null); // Dùng để quản lý việc sửa

  const navigate = useNavigate();

  useEffect(() => {
    fetchPhones();
    fetchDepartments();
    setIsAdmin(isAdminLoggedIn()); // kiểm tra login mỗi lần load
  }, []);

  const fetchPhones = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/phones');
      setPhones(res.data);
    } catch (err) {
      console.error('Lỗi fetch phones', err);
    }
  };

  const fetchDepartments = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/departments');
      setDepartments(res.data);
    } catch (err) {
      console.error('Lỗi fetch departments', err);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    const query = `http://localhost:5000/api/phones/search?keyword=${keyword}&department=${selectedDept}`;
    const res = await axios.get(query);
    setPhones(res.data);
  };

  const handleDeptChange = async (e) => {
    const dept = e.target.value;
    setSelectedDept(dept);

    const query = `http://localhost:5000/api/phones/search?keyword=${keyword}&department=${dept}`;
    const res = await axios.get(query);
    setPhones(res.data);
  };

  const handleEdit = (phone) => {
    setEditingPhone(phone);
    // Điều hướng đến trang sửa (có thể là Modal hoặc trang riêng)
    navigate(`/edit-phone/${phone.id}`);
  };

  const handleDelete = (id) => {
    if (window.confirm('Bạn có chắc chắn muốn xóa số điện thoại này?')) {
        axios.delete(`http://localhost:5000/api/phones/${id}`)
            .then(response => {
                // Cập nhật lại danh sách sau khi xóa
                setPhones(phones.filter(phone => phone.id !== id));
                alert('Xóa thành công');
            })
            .catch(error => console.error(error));
    }
};


  const handleLogout = () => {
    toast.success('Đăng xuất thành công!');
    logoutAdmin(); // Xử lý đăng xuất và chuyển hướng về trang login
  };

  return (
    <>
      {/* Navbar */}
      <nav className="navbar navbar-expand-lg navbar-light bg-light fixed-top shadow-sm">
        <div className="container-fluid">
          <Link className="navbar-brand fw-bold" to="/">PhoneBook</Link>

          <div className="collapse navbar-collapse" id="navbarContent">
            {/* Search form */}
            <form className="d-flex ms-3 flex-grow-1" onSubmit={handleSearch}>
              <input
                type="text"
                className="form-control me-2"
                placeholder="Tìm kiếm theo tên, số điện thoại, bộ phận, vị trí..."
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)}
              />
              <select
                className="form-select me-2"
                style={{ maxWidth: '450px' }}
                value={selectedDept}
                onChange={handleDeptChange}
              >
                <option value="">Tất cả bộ phận</option>
                {departments.map((dept) => (
                  <option key={dept.id} value={dept.name}>
                    {dept.name}
                  </option>
                ))}
              </select>
              <button className="btn btn-outline-primary" type="submit">Tìm</button>
            </form>

            {/* Admin Info */}
            <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
              {isAdmin ? (
                <>
                  <li className="nav-item d-flex align-items-center me-3">
                    <span className="nav-link">Administrator</span>
                  </li>
                  <li className="nav-item">
                    <Link className="btn btn-outline-info" to="/dashboard">Dashboard</Link>
                  </li>
                  <li className="nav-item">
                    <button className="btn btn-outline-danger" onClick={handleLogout}>
                      Đăng xuất
                    </button>
                  </li>
                </>
              ) : (
                <li className="nav-item">
                  <Link className="btn btn-outline-success" to="/login">
                    Đăng nhập
                  </Link>
                </li>
              )}
            </ul>
          </div>
        </div>
      </nav>

      {/* Nội dung */}
      <div className="container py-5 mt-4">
        <div className="row">
          {phones.map((phone) => (
            <div key={phone.id} className="col-md-3 mb-3">
              <PhoneCard
                phone={phone}
                isAdmin={isAdmin}
                onEdit={() => handleEdit(phone)}
                onDelete={() => handleDelete(phone.id)}
              />
            </div>
          ))}
        </div>
      </div>
      {editingPhone && (
                <div>
                    <h3>Sửa thông tin điện thoại</h3>
                    <form onSubmit={handleUpdate}>
                        <div>
                            <label>Số điện thoại</label>
                            <input 
                                type="text" 
                                value={editingPhone.phone_number} 
                                onChange={(e) => setEditingPhone({...editingPhone, phone_number: e.target.value})}
                            />
                        </div>
                        <div>
                            <label>Vị trí</label>
                            <input 
                                type="text" 
                                value={editingPhone.location} 
                                onChange={(e) => setEditingPhone({...editingPhone, location: e.target.value})}
                            />
                        </div>
                        <div>
                            <label>Phòng ban</label>
                            <input 
                                type="text" 
                                value={editingPhone.department_name} 
                                onChange={(e) => setEditingPhone({...editingPhone, department_name: e.target.value})}
                            />
                        </div>
                        <button type="submit">Cập nhật</button>
                    </form>
                </div>
            )}

    </>
  );
}

export default PhoneList;
