import React from 'react';
import { Link } from 'react-router-dom';

const Navbar = ({ keyword, setKeyword, departments, selectedDept, handleDeptChange, handleSearch, isAdmin, adminName, handleLogout }) => {
  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light fixed-top shadow-sm">
      <div className="container-fluid">
        <Link className="navbar-brand fw-bold" to="/">PhoneBook</Link>

        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarContent">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarContent">
          {/* Search form */}
          <form className="d-flex ms-3 flex-grow-1" onSubmit={handleSearch}>
            <input
              className="form-control me-2"
              type="search"
              placeholder="Tìm tên, số điện thoại, bộ phận..."
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
            />
            <select 
              className="form-select me-2"
              style={{ maxWidth: '200px' }}
              value={selectedDept}
              onChange={handleDeptChange}
            >
              <option value="">Tất cả bộ phận</option>
              {departments.map((dept) => (
                <option key={dept.id} value={dept.name}>
                  {dept.name}
                </option>
              ))}
            </select>
            <button className="btn btn-outline-primary" type="submit">Tìm</button>
          </form>

          {/* Admin Info */}
          <ul className="navbar-nav ms-auto mb-2 mb-lg-0">
            {isAdmin ? (
              <>
                <li className="nav-item d-flex align-items-center me-3">
                  <span className="nav-link">{adminName}</span>
                </li>
                <li className="nav-item">
                  <button className="btn btn-outline-danger" onClick={handleLogout}>
                    Đăng xuất
                  </button>
                </li>
              </>
            ) : (
              <li className="nav-item">
                <Link className="btn btn-outline-success" to="/login">
                  Đăng nhập
                </Link>
              </li>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
