// src/pages/PhoneList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import PhoneCard from '../components/PhoneCard';

const PhoneList = () => {
  const [phones, setPhones] = useState([]);
  const [departmentFilter, setDepartmentFilter] = useState('');
  const [locationFilter, setLocationFilter] = useState('');
  const [isAdmin, setIsAdmin] = useState(false);

  useEffect(() => {
    fetchPhones();
  }, [departmentFilter, locationFilter]);  // mỗi lần filter thay đổi sẽ tự fetch lại

  const fetchPhones = async () => {
    try {
      const response = await axios.get('/api/phones', {
        params: {
          department: departmentFilter,
          location: locationFilter,
        },
      });
      setPhones(response.data);
    } catch (error) {
      console.error('Lỗi fetch phones:', error);
    }
  };

  const handleEdit = (phone) => {
    console.log('Sửa', phone);
    // mở modal edit ở đây nếu muốn
  };

  const handleDelete = (id) => {
    console.log('Xóa', id);
    // confirm rồi gọi API xóa ở đây nếu muốn
  };

  return (
    <div className="container mt-4">
      {/* Filter */}
      <div className="filters sticky-top bg-white py-3 mb-4">
        <div className="row">
          <div className="col-md-6">
            <select className="form-select" value={departmentFilter} onChange={(e) => setDepartmentFilter(e.target.value)}>
              <option value="">Tất cả bộ phận</option>
              <option value="Nhân sự">Nhân sự</option>
              <option value="IT">IT</option>
              {/* thêm options nếu có */}
            </select>
          </div>

          <div className="col-md-6">
            <select className="form-select" value={locationFilter} onChange={(e) => setLocationFilter(e.target.value)}>
              <option value="">Tất cả vị trí</option>
              <option value="Lầu 2 phòng nhân sự">Lầu 2 phòng nhân sự</option>
              <option value="Lầu 3 phòng IT">Lầu 3 phòng IT</option>
              {/* thêm options nếu có */}
            </select>
          </div>
        </div>
      </div>

      {/* Phone cards */}
      <div className="row">
        {phones.map((phone) => (
          <PhoneCard
            key={phone.id}
            phone={phone}
            isAdmin={isAdmin}
            onEdit={handleEdit}
            onDelete={handleDelete}
          />
        ))}
      </div>
    </div>
  );
};

export default PhoneList;
