import React from 'react';
import Navbar from '../components/Navbar';

const PhonesManagement = () => {
    const [phones, setPhones] = useState([]);
      const [departments, setDepartments] = useState([]);
      const [selectedDept, setSelectedDept] = useState('');
      const [keyword, setKeyword] = useState('');
      const [isAdmin, setIsAdmin] = useState(false);
      const [editingPhone, setEditingPhone] = useState(null); // Dùng để quản lý việc sửa

    return (
        <><>
        {/* Navbar dùng component mới */}
        <Navbar
          departments={departments}
          keyword={keyword}
          setKeyword={setKeyword}
          selectedDept={selectedDept}
          setSelectedDept={setSelectedDept}
          isAdmin={isAdmin}
          handleSearch={handleSearch}
          handleDeptChange={handleDeptChange}
        />

        {/* Noi dung phonemanagement */}
        </><div className="container mt-4">
                <h2>Phones Management</h2>
                <form className="mb-4">
                    <input
                        type="text"
                        className="form-control mb-2"
                        placeholder="Số điện thoại mới" />
                    <select
                        className="form-control mb-2"
                        placeholder="Chọn bộ phận">
                    </select>
                </form>

                <table className="table">
                    <thead>
                        <tr>
                            <th>Tên người dùng</th>
                            <th>Số điện thoại</th>
                            <th>Hành động</th>
                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>Nguyễn Văn A</td>
                            <td>0123456789</td>
                            <td>
                                <button className="btn btn-sm btn-warning me-2">Sửa</button>
                                <button className="btn btn-sm btn-danger">Xóa</button>
                            </td>
                        </tr>
                    </tbody>
                </table>
            </div></>
    );
};

export default PhonesManagement;
